function Header() {
    return (
    <div className={stylesFlex}>Header</div>
)}

function Footer() {
    return (
    <div className={stylesFlex}>Footer</div>
);
}

function App() {
    return (
        <div className='min-h-[100vh] bg-slate-800'>
            <Header />
            <h1 
            className={`${stylesFlex} text-5xl bg-slate-500 text-stone-400 font-bold underline text-center min-h-[500px] h-[80vh]`}>
                Hello world!
            </h1>
            <Footer />
        </div>
    );
}

export default App;
